<script>
import { mapActions } from 'pinia'
import WaveSVG from './svg/homebar/WaveSVG.vue'
import { useUserStore } from '~/stores/user'
import { HeadsetType } from '~/stores/interfaces/HeadsetType'
import { ANC } from '~/stores/interfaces/ANC'
import { useAudioStore } from '~/stores/audio'

export default {
  name: 'HomeBar',
  components: {
    WaveSVG
  },
  props: {
    title: {
      type: String,
      default: 'Homebar'
    }
  },
  emits: ['update:soundscape'],
  setup () {
    const { t } = useI18n()
    const localePath = useLocalePath()
    return { t, localePath }
  },
  data () {
    return {
      adaptiveSounscape: false,
      form: {
        soundscape: '',
        headphone_type: '',
        anc_type: '',
        adaptive_sound_scape: ''
      }
    }
  },
  mounted () {
    this.fetchSettings()
    this.updateActiveSoundscape()
    window.addEventListener('keydown', event => this.handleKeyDown(event))
    navigator.mediaSession.setActionHandler('play', this.playSound())
  },
  methods: {
    ...mapActions(useUserStore, ['updateHeadsetType', 'updateANC', 'logout']),
    ...mapActions(useAudioStore, ['isPlaying', 'setPlaying']),
    updateActiveSoundscape () {
      const soundscape = this.form.soundscape
      switch (soundscape) {
        case 'Lagoon':
          document.getElementById('lagoonLinkHomebar').setAttribute('active')
          break
        case 'x':
          document.getElementById('tropicsLinkHomebar').setAttribute('active')
          break
        case 'Forest':
          document.getElementById('forestLinkHomebar').setAttribute('active')
          break
        case 'Meadow':
          document.getElementById('meadowLinkHomebar').setAttribute('active')
          break
      }
    },
    handleKeyDown (e) {
      if (e.code === 'Space' && e.code === 'Space2') {
        e.preventDefault() // Prevent the default action (scrolling)
        this.setPlaying(!this.isPlaying())
      }
    },
    playSound () {
      this.setPlaying(!this.isPlaying())
    },
    fetchSettings () {
      this.$axios.post('/api/fetch-settings').then(({ data }) => {
        if (data.success) {
          this.form = data.setting
          this.$emit('update:soundscape', this.form.soundscape)
          if (data.setting.adaptive_sound_scape === 'yes' || data.setting.adaptive_sound_scape === 'Yes') {
            this.adaptiveSounscape = true
          } else {
            this.adaptiveSounscape = false
          }
        }
      }).catch(() => {
        if (useUserStore().offlineMode) { // Offline mode active
          this.$toast.warn('Offline mode is active. Your changes will be temporarily.')
        } else {
          this.$toast.error('Something went wrong while saving... try again later')
          this.logout()
          // this.audioCtx.close()
          this.$router.push(this.localePath('/auth/login'))
        }
      })
    },

    saveSetting (value, type) {
      if (type === 'soundscape') {
        // console.log('VALUE VALUE' + value)
        this.form.soundscape = value
        this.$emit('update:soundscape', value)
      }
      if (type === 'headphone') {
        this.form.headphone_type = value
        if (value === 'In-ear') { this.updateHeadsetType(HeadsetType.InEar) } else { this.updateHeadsetType(HeadsetType.OverEar) }
      }
      if (type === 'anc') {
        this.form.anc_type = value
        if (value === 'yes' || value === 'Yes') { this.updateANC(ANC.Yes) } else { this.updateANC(ANC.No) }
      }
      if (type === 'adaptivesoundscap') {
        if (this.adaptiveSounscape === true) {
          this.form.adaptive_sound_scape = 'no'
        } else {
          this.form.adaptive_sound_scape = 'yes'
        }
      }

      this.$axios.post('/api/update-setting', this.form).then(({ data }) => {
        if (data.success) {
          // this.$toast.success(data.message)
          this.form = data.setting
          this.updateActiveSoundscape()
        }
      }).catch(() => {
        this.$toast.error('save: something went wrong while saving...')
      })
    }
  }
}
</script>
